import React, { PureComponent } from "react";
import PropTypes from "prop-types";
import Typography from '@material-ui/core/Typography';
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import { Grid, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import Icon from '@material-ui/core/Icon';
import IconButton from '@material-ui/core/IconButton';
import { navContent } from './navContent';
import { createTheme } from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme) => ({
        root: {
          flexGrow: 1,
        },
        sectionTitle: {
            color: '#FFFFFF',
            fontSize: 20,
            marginLeft: 10,
            paddingTop:14,
        },
        sideNav:{
                position: 'absolute',
                height: '100%',
        },
        navButton:{
                marginLeft: 10,
                marginRight: 10,
                minWidth:0,
                paddingLeft:7,
                paddingRight:7,
                borderRadius:10,
                color: '#fff'
        },
        navTitle:{
                fontSize:14,
                marginLeft: 10,
                marginRight: 10,
                lineHeight:'48px',
                color: '#808191',
                fontFamily: 'Poppins'
        },
        title:{
                fontSize:35,
                fontFamily: 'Poppins',
                color: '#fff',
                marginTop:10,
                marginLeft: 10,
                fontWeight:500
        },
        subHeading:{
                fontSize:11,
                fontFamily: 'Poppins',
                marginTop:50,
                marginBottom:15,
                marginLeft: 15,
                color: '#808191',
                fontWeight: 500
        },
        navItem:{
                marginBottom:30
        },
        navDivider:{
                color:'#fff',
                width:'60%'
        }


      }));

      

export default function SideButton(){

        const theme = createTheme({
                palette: {
                  primary: {
                    light: '#757ce8',
                    main: '#353440',
                    dark: '#FF7551',
                    contrastText: '#808191',
                  },
                  secondary: {
                    light: '#ff7961',
                    main: '#f44336',
                    dark: '#ba000d',
                    contrastText: '#000',
                  },
                },
              });

        const classes = useStyles();
        return (
                <ThemeProvider theme={theme}>
                
                <Grid
                        container
                        direction="column"
                        justifyContent="flex-start"
                        alignItems="flex-start"
                        xs={2}
                        className={classes.sideNav}
                >
                        <Typography className={classes.title} variant="h2">
                        oliver smith
                        </Typography>
                        <Typography className={classes.subHeading}>
                        MENU
                        </Typography>
                        <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />


                        {navContent.map(item => 
                        <Grid
                        container
                        direction="row"
                        justifyContent="flex-start"
                        alignItems="center"
                        className={classes.navItem}>
                                <Button variant="contained" className={classes.navButton} color="primary"><Icon>{item.icon}</Icon></Button>
                                <span className={classes.navTitle}>{item.title}</span>
                                
                        </Grid>
                        )}
                <Divider variant='middle' className={classes.navDivider} light></Divider>
                </Grid>
                </ThemeProvider>
        );
}
